const feedbackForm=document.querySelector(".main-feedback__form"),fields=feedbackForm.querySelectorAll(".feedback-field"),fullname=feedbackForm.querySelector("[name=fullname]"),phone=feedbackForm.querySelector("[name=phone]");feedbackForm.addEventListener("submit",(function(e){if(!fullname.value||!phone.value){e.preventDefault();for(let e=0;e<fields.length;e++){let l=fields[e];l.value?l.classList.remove("error"):l.classList.add("error")}}}));